mapper.select:
SELECT news_id, type, title, time, fixed_recommend, is_recommend, img_address,
unit_price, total_price, reading_number, now_total_price, status, manager_id FROM news_tb
AND title like '%' #{title} '%'
AND manager_id=#{managerId}
ORDER BY ${orderName} ${orderWay} LIMIT #{pageNum},#{pageSize}

SELECT news_id, type, title, time, fixed_recommend, is_recommend, img_address,
unit_price, total_price, reading_number, now_total_price, status, manager_id FROM news_tb
AND type=#{type}
AND manager_id=#{managerId}
ORDER BY ${orderName} ${orderWay} LIMIT #{pageNum},#{pageSize}

SELECT news_id, type, title, time, fixed_recommend, is_recommend, img_address,
unit_price, total_price, reading_number, now_total_price, status, manager_id FROM news_tb
AND type=#{type}
AND manager_id=#{managerId}
ORDER BY ${orderName} ${orderWay}

SELECT news_id, type, title, time, fixed_recommend, is_recommend, img_address,
unit_price, total_price, reading_number, now_total_price, status, manager_id FROM news_tb
fixed_recommend=${fixedRecommend}
AND manager_id=#{managerId}
ORDER BY time desc LIMIT #{pageNum},#{pageSize};

SELECT news_id, type, title, time, fixed_recommend, is_recommend, img_address,
unit_price, total_price, reading_number, now_total_price, status, manager_id FROM news_tb
is_recommend=${isRecommend}
AND manager_id=#{managerId}
ORDER BY time desc LIMIT #{pageNum},#{pageSize};

SELECT distinct type from news_tb
type!='' AND type is not null
AND manager_id=#{managerId}
